import { Typography, Box, makeStyles, Grid, TextField, Button } from "@material-ui/core"
import { blueGrey, green } from '@material-ui/core/colors';
import {useState, useEffect, } from "react";
import { useHistory, useParams } from "react-router-dom";
import axios from "axios";
import './style.css';

const useStyles = makeStyles({
 headingColor: {
  backgroundColor: blueGrey[900],
  color: "white"
 },
 addStuColor: {
  backgroundColor: green[400],
  color: "white"
 },

});

const Edit = () => {
 const [showSuccess, setShowSuccess] = useState(false);
 const successMessage = 'Usuarios editados con éxito';
 const classes = useStyles();
 const { id } = useParams();
 const history = useHistory();
 const [student, setStudent] = useState({
  stuname: "",
  email: ""
 });
 useEffect(() => {
  async function getStudent() {
   try {
    const student = await axios.get(`http://localhost:3333/students/${id}`)
    // console.log(student.data);
    setStudent(student.data);
   } catch (error) {
    console.log("Something is Wrong");
   }
  }
  getStudent();
 }, [id]);

 function onTextFieldChange(e) {
  setStudent({
   ...student,
   [e.target.name]: e.target.value
  })
 }

 async function onFormSubmit(e) {
  e.preventDefault()
  try {
   await axios.put(`http://localhost:3333/students/${id}`, student)
   history.push("/")
  } catch (error) {
   console.log("Something is Wrong");
  }

 }
 function handleClick() {
  history.push("/")
 }
 return (
  <>
   <Box textAlign="center" p={2} className={classes.headingColor} mb={2}>



    <Typography variant="h2"> Sesion de Edicion
    </Typography>


   </Box>

   <Grid class="container" style={{ marginLeft: '20px' }} container justify="center" spacing={4}>
    <Grid  item md={6} xs={12}>
      <Grid class="formu_">
       <Grid style={{ marginLeft: '20px' }} class="input" item xs={12} sm={6}>
        <TextField autoComplete="id" name="id"  required fullWidth id="id" label="ID" autoFocus value={id} disabled />
       </Grid>
       <Grid style={{ marginLeft: '20px' }} class="input" item xs={12} sm={6}>
        <TextField autoComplete="stuname" name="stuname" required fullWidth id="stuname" label="Name" value={student.stuname} onChange={e => onTextFieldChange(e)} />
       </Grid>
       <Grid style={{ marginLeft: '20px' }} class="input" item xs={12}>
        <TextField autoComplete="email" name="email"  required fullWidth id="email" label="Email Address" value={student.email} onChange={e => onTextFieldChange(e)} />
       </Grid>
       < Box m={3}>
       <Button class="shadow__btn"  type="button"  fullWidth onClick={e => onFormSubmit(e)}>Actualizar</Button>
      </Box>
      </Grid>
     </Grid> 
     </Grid > 
     <Box  m={3} textAlign="center">
      <Button class="btn" onClick={handleClick}>Volver al inicio</Button>
     </Box>
  </>

 )
}


export default Edit
